ValidationRule.errorConditionFormula:
If(	
	OR(
			(NOT($Permission.Custom_Permission_for_SAP_Field) || $User.Id="005C0000009SZVQ" || $User.Id="005C0000009QipC" || $User.Id="005C0000009SZaf"),
			ISPICKVAL(Employee_Non_Employee__c,"No")							
		),false,(	
					IF(  RecordTypeId!='012800000002DoB',false,
						OR 
						(
						  NOT(ISNUMBER(SAP__c )), 
						  AND(ISNUMBER(SAP__c),OR((LEN(SAP__c) < 5),(LEN(SAP__c) >8)))
						)
					)
				)
)
