CustomField.formula:
If( OR(
AND( MONTH(TODAY())==1, Date__c >= DATE( YEAR( TODAY() )-1,12,01) , Date__c < DATE( YEAR( TODAY()) ,01,01)
) , AND ( MONTH(TODAY())==1, Date__c >= DATE( YEAR( TODAY() )-2,12,01) , Date__c < DATE( YEAR( TODAY())-1 ,01,01) ),
AND( MONTH(TODAY())!=1, Date__c >= DATE( YEAR( TODAY() ),MONTH(TODAY())-1,01) , Date__c < DATE( YEAR( TODAY()) ,MONTH(TODAY()),01)
) , AND ( MONTH(TODAY())!=1, Date__c >= DATE( YEAR( TODAY() )-1,MONTH(TODAY())-1,01) , Date__c < DATE( YEAR( TODAY())-1 ,MONTH(TODAY()),01) )
)
, TRUE, FALSE )
